import React from "react";
import styled from "styled-components";
import ThumbUpOutlinedIcon from "@mui/icons-material/ThumbUpOutlined";
import ThumbDownOutlinedIcon from "@mui/icons-material/ThumbDownOutlined";
import ReplyOutlinedIcon from '@mui/icons-material/ReplyOutlined';
import SaveOutlinedIcon from '@mui/icons-material/SaveOutlined';

const Container = styled.div`
    display: flex;
    gap: 24px;
`;
const Content = styled.div`
    flex: 5;
`;
const VideoWrapper = styled.div`
    flex: 5;
`;

const Title = styled.h1`
    font-size: 18px;
    font-weight: 400;
    margin-top: 20px;
    margin-bottom: 10px;
    color: ${({ theme }) => theme.text};
`;

const Details = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
`;

const Info = styled.span`
    color: ${({ theme }) => theme.textSoft};
`;

const Buttons = styled.div`
    display: flex;
    gap: 20px;
    color: ${({ theme }) => theme.text};
`;

const Button = styled.div`
displ
`;

const Recommandation = styled.div`
    flex: 2;
`;

const Video = () => {
    return (
        <Container>
            <Content>
                <VideoWrapper>
                    <iframe width="100%" height="720" src="https://www.youtube.com/embed/UNYbxtCGqEk" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                </VideoWrapper>
                <Title>Test Title</Title>
                <Details>
                    <Info>3,256,765 views - 05 Jul 2022</Info>
                    <Buttons>
                        <Button>
                            <ThumbUpOutlinedIcon />
                            500
                        </Button>
                        <Button>
                            <ThumbDownOutlinedIcon />
                            Dislike
                        </Button>
                        <Button>
                            <ReplyOutlinedIcon />
                            Share
                        </Button>
                        <Button>
                            <SaveOutlinedIcon />
                            Save
                        </Button>
                    </Buttons>
                </Details>
            </Content>
            <Recommandation>Recommandation</Recommandation>
        </Container>
    );
};

export default Video;
